import PostList from "./components/PostList";
import FormButton from "./components/ui/FormButton";
import FormInput from "./components/ui/input/FormInput";

export default function App(){
    let [posts, setPosts] = useState([
        {'id':1, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':2, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':3, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':4, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':5, 'title': 'Язык Python', 'body': 'Язык программирования'}
    ])
    const [title, setTitle] = useState('') 

    function addNewPost(e) {
        e.preventDefault()
    }


    return(
        <div>
            <form action="">
                <FormInput val type="text" placeholder='Название'/>
                <FormInput type="text" placeholder='Содержимое'/>
                <FormButton>Создать пост</FormButton>
            </form>
            <PostList onClick={addNewPost} posts={posts} title='Список постов'/>
        </div>
        )
}
