import React, {useState} from "react";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import FormSelect from "./components/ui/selected/FormSelect";
import FormInput from "./components/ui/input/FormInput";

export default function App(){
    let [posts, setPosts] = useState([])
    let [sortedPosts, setSortedPosts] = useState('')
    let [searchQuery, setSearchQuery] = useState('')

    const sortedPostList = getSortedPostList

    const createPost = (newPost) => {
        setPosts([...posts, newPost])
    }

    const removePost = (post) =>{
        setPosts(posts.filter(p=> p.id !== post.id))
    }

    const sortPosts = (sort)=>{
        setSortedPosts(sort);
        console.log(sort)

    }

    const getSortedPostList = ()=>{
        if(sortedPosts) {
            return setPosts([...posts].sort( (a,b) => a[sortedPosts].localeCompare(b[sortedPosts]) ))
        }
    }

    return(
        <div>
            <FormInput
             value = {searchQuery}
             onChange = {e => setSearchQuery(e.target.value)}
             placeholder = 'Поиск' />
            <FormSelect
                value = {sortedPosts}
                onChange = {sortPosts}
                defaultValue='Сортировка' options={[
                {value: 'title', name:'По названию'},
                {value: 'body', name:'По содержанию'}
            ]}/>
            <PostForm create = {createPost}/>
            <PostList remove={removePost} posts={posts} title='Список постов'/>
        </div>
        )
}
