import React, {useState} from "react";
import PostList from "./components/PostList";
import FormButton from "./components/ui/FormButton";
import FormInput from "./components/ui/input/FormInput";

export default function App(){
    let [posts, setPosts] = useState([
        {'id':1, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':2, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':3, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':4, 'title': 'Язык Python', 'body': 'Язык программирования'},
        {'id':5, 'title': 'Язык Python', 'body': 'Язык программирования'}
    ])
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('') 

    const addNewPost = (e) => {
        e.preventDefault()
        const newPost = {
            id: Date.now(),
            title,
            body
        }
        setPosts([...posts, newPost])
        setTitle('')
        setBody()
    }


    return(
        <div>
            <form >
                <FormInput 
                    value={title} 
                    onChange={e => setTitle(e.target.value)}
                    type="text" 
                    placeholder='Название'
                    />
                <FormInput 
                    value={body}
                    onChange = {e => setBody(e.target.value)} 
                    type="text" 
                    placeholder='Содержимое'
                    />
                <FormButton onClick={addNewPost} >Создать пост</FormButton>
            </form>
            <PostList posts={posts} title='Список постов'/>
        </div>
        )
}
